}}}); new OverText('apellidos', {positionOptions: {offset: {x: 4, y: 4}}}); new OverText('telefono', {positionOptions: {offset: {x: 4, y: 4}}}); new OverText('movil', {positionOptions: {offset: {x: 4, y: 4}}}); new OverText('direccion', {positionOptions: {offset: {x: 4, y: 4}}}); new OverText('referencia', {positionOptions: {offset: {x: 4, y: 4}}});// Evento de cambio de servicio, para generar cambio de asunto if ($('servicio<?php  echo($transaccion); ?>')) {  $('servicio<?php  echo($transaccion); ?>').addEvent('change', function(e) {   actualizacion_causal<?php  echo($transaccion); ?>();  }); } if ($('categoria<?php  echo($transaccion); ?>')) {  $('categoria<?php  echo($transaccion); ?>').addEvent('change', function(e) {   actualizacion_causal<?php  echo($transaccion); ?>();  }); } function actualizacion_causal<?php  echo($transaccion); ?>() {  var servicio = $('servicio<?php  echo($transaccion); ?>').getElement(':selected').value;  var categoria = $('categoria<?php  echo($transaccion); ?>').getElement(':selected').value;  $("divCausal<?php  echo($transaccion); ?>").empty();  if (categoria != "01" && categoria != "04") {   $("cellCausal<?php  echo($transaccion); ?>").hide();  } else {   $("cellCausal<?php  echo($transaccion); ?>").show();  }  var parametros = {'servicio': servicio, 'categoria': categoria};  var datos = JSON.encode(parametros);  new Request.JSON({   url: 'modulos/solicitudes/consultas/jsons/causales.json.php',   data: "datos=" + datos,   requestOptions: {    spinnerOptions: {     message: 'Actualizando Causales...'    }   },   onRequest: function() {    $('spinner').show();    if ($('<?php  echo($formulario->interno ); ?>') && MUI.options.standardEffects == true) {     $('<?php  echo($formulario->interno ); ?>').setStyle('opacity', 1).get('morph').start({'opacity': 0});    }    MUI.Notificacion("Actualizando...");   },   onComplete: function(djson) {    $('spinner').hide();    if ($('<?php  echo($formulario->interno ); ?>') && MUI.options.standardEffects == true) {     $('<?php  echo($formulario->interno ); ?>').setStyle('opacity', 0).get('morph').start({'opacity': 1});    }    var objeto = djson.objeto;    var dhtml = djson.html;    new Element('div', {html: dhtml}).inject('divCausal<?php  echo($transaccion); ?>', 'top');   }  }).send(); }</script>